package com.example.learnlog.data;

import androidx.room.Database;
import androidx.room.RoomDatabase;
import androidx.room.migration.Migration;
import androidx.sqlite.db.SupportSQLiteDatabase;

@Database(entities = {TaskEntity.class}, version = 2, exportSchema = false)
public abstract class AppDatabase extends RoomDatabase {
    public abstract TaskDao taskDao();

    public static final Migration MIGRATION_1_2 = new Migration(1, 2) {
        @Override
        public void migrate(SupportSQLiteDatabase database) {
            database.execSQL("CREATE TABLE IF NOT EXISTS `tasks` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, `title` TEXT, `subject` TEXT, `dueAt` INTEGER, `priority` INTEGER NOT NULL, `status` TEXT, `progress` INTEGER NOT NULL, `completed` INTEGER NOT NULL, `notes` TEXT, `createdAt` INTEGER NOT NULL, `updatedAt` INTEGER NOT NULL)");
        }
    };
}

package com.example.learnlog.data;

import androidx.room.Dao;
import androidx.room.Insert;
import androidx.room.OnConflictStrategy;
import androidx.room.Query;
import androidx.room.Update;
import kotlinx.coroutines.flow.Flow;
import java.util.List;

@Dao
public interface TaskDao {
    @Query("SELECT * FROM tasks")
    Flow<List<TaskEntity>> getAll();

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insert(task: TaskEntity): Long;

    @Update
    suspend fun update(task: TaskEntity);

    @Query("DELETE FROM tasks WHERE id = :id")
    suspend fun delete(id: Long);
}

package com.example.learnlog.data;

import androidx.room.Entity;
import androidx.room.PrimaryKey;

@Entity(tableName = "tasks")
public class TaskEntity {
    @PrimaryKey(autoGenerate = true)
    public long id;
    public String title;
    public String subject;
    public Long dueAt;
    public int priority; // 0: low, 1: med, 2: high
    public String status; // PENDING, IN_PROGRESS, COMPLETED
    public int progress;
    public boolean completed;
    public String notes;
    public long createdAt;
    public long updatedAt;
}

package com.example.learnlog.data;

import com.example.learnlog.data.TaskDao;
import com.example.learnlog.data.TaskEntity;
import java.util.List;
import javax.inject.Inject;
import javax.inject.Singleton;
import kotlinx.coroutines.flow.Flow;

@Singleton
public class TaskRepository {
    private final TaskDao taskDao;

    @Inject
    public TaskRepository(TaskDao taskDao) {
        this.taskDao = taskDao;
    }

    public Flow<List<TaskEntity>> getTasks() {
        return taskDao.getAll();
    }

    public suspend fun insert(task: TaskEntity) {
        taskDao.insert(task);
    }

    public suspend fun update(task: TaskEntity) {
        taskDao.update(task);
    }

    public suspend fun delete(id: Long) {
        taskDao.delete(id);
    }
}

package com.example.learnlog.data;

import android.content.Context;
import androidx.datastore.core.DataStore;
import androidx.datastore.preferences.core.Preferences;
import androidx.datastore.preferences.core.edit;
import androidx.datastore.preferences.core.stringPreferencesKey;
import androidx.datastore.preferences.preferencesDataStore;
import com.example.learnlog.ui.tasks.TaskFilter;
import com.example.learnlog.ui.tasks.TaskSort;
import java.io.IOException;
import javax.inject.Inject;
import kotlinx.coroutines.flow.Flow;
import kotlinx.coroutines.flow.catch;
import kotlinx.coroutines.flow.map;

public class UserPreferencesRepository {

    private static final String USER_PREFERENCES_NAME = "user_preferences";
    private static final Context.Key<DataStore<Preferences>> PREFERENCES_KEY =
            new Context.Key<>(USER_PREFERENCES_NAME);

    private final DataStore<Preferences> dataStore;

    private static final Preferences.Key<String> TASK_FILTER_KEY = stringPreferencesKey("task_filter");
    private static final Preferences.Key<String> TASK_SORT_KEY = stringPreferencesKey("task_sort");

    @Inject
    public UserPreferencesRepository(Context context) {
        this.dataStore = context.getDataStore();
    }

    public Flow<TaskFilter> getTaskFilter() {
        return dataStore.data()
                .catch((exception) -> {
                    if (exception instanceof IOException) {
                        emit(Preferences.EMPTY);
                    } else {
                        throw exception;
                    }
                })
                .map(preferences -> {
                    String filterName = preferences.get(TASK_FILTER_KEY);
                    return filterName != null ? TaskFilter.valueOf(filterName) : TaskFilter.ALL;
                });
    }

    public suspend fun setTaskFilter(filter: TaskFilter) {
        dataStore.edit(preferences -> {
            preferences.set(TASK_FILTER_KEY, filter.name());
            return null;
        });
    }

    public Flow<TaskSort> getTaskSort() {
        return dataStore.data()
                .catch((exception) -> {
                    if (exception instanceof IOException) {
                        emit(Preferences.EMPTY);
                    } else {
                        throw exception;
                    }
                })
                .map(preferences -> {
                    String sortName = preferences.get(TASK_SORT_KEY);
                    return sortName != null ? TaskSort.valueOf(sortName) : TaskSort.DUE_DATE;
                });
    }

    public suspend fun setTaskSort(sort: TaskSort) {
        dataStore.edit(preferences -> {
            preferences.set(TASK_SORT_KEY, sort.name());
            return null;
        });
    }

    private static final Context.Getter<DataStore<Preferences>> dataStoreGetter =
            (context) -> preferencesDataStore(USER_PREFERENCES_NAME).getValue(context, null);

    private static Context.Key<DataStore<Preferences>> getDataStoreKey() {
        return PREFERENCES_KEY;
    }
}

