package com.example.learnlog.di;

import android.content.Context;
import androidx.room.Room;
import com.example.learnlog.data.AppDatabase;
import com.example.learnlog.data.TaskDao;
import dagger.Module;
import dagger.Provides;
import dagger.hilt.InstallIn;
import dagger.hilt.android.qualifiers.ApplicationContext;
import dagger.hilt.components.SingletonComponent;
import javax.inject.Singleton;

@Module
@InstallIn(SingletonComponent.class)
public class DatabaseModule {

    @Provides
    @Singleton
    public AppDatabase provideAppDatabase(@ApplicationContext Context context) {
        return Room.databaseBuilder(context, AppDatabase.class, "learnlog-db")
                .fallbackToDestructiveMigration() // For dev; replace with proper migration for prod
                .build();
    }

    @Provides
    public TaskDao provideTaskDao(AppDatabase appDatabase) {
        return appDatabase.taskDao();
    }
}

package com.example.learnlog.ui.tasks;

import android.app.DatePickerDialog;
import android.app.TimePickerDialog;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;
import androidx.annotation.Nullable;
import androidx.fragment.app.viewModels;
import com.example.learnlog.data.TaskEntity;
import com.example.learnlog.databinding.DialogAddTaskBinding;
import com.google.android.material.bottomsheet.BottomSheetDialogFragment;
import java.text.SimpleDateFormat;
import java.util.Calendar;
import java.util.Locale;

public class AddEditTaskBottomSheet extends BottomSheetDialogFragment {

    private DialogAddTaskBinding binding;
    private final TasksViewModel viewModel;
    private TaskEntity currentTask;
    private Calendar dueDateTime = Calendar.getInstance();

    public AddEditTaskBottomSheet(TasksViewModel viewModel, @Nullable TaskEntity task) {
        this.viewModel = viewModel;
        this.currentTask = task;
        if (task != null && task.dueAt != null) {
            dueDateTime.setTimeInMillis(task.dueAt);
        }
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        binding = DialogAddTaskBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(View view, Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        if (currentTask != null) {
            binding.titleInput.setText(currentTask.title);
            binding.subjectInput.setText(currentTask.subject);
            binding.notesInput.setText(currentTask.notes);
            binding.priorityGroup.check(getPriorityChipId(currentTask.priority));
            updateDateTimeFields();
        }

        binding.datePicker.setOnClickListener(v -> showDatePicker());
        binding.timePicker.setOnClickListener(v -> showTimePicker());

        binding.btnSave.setOnClickListener(v -> saveTask());
    }

    private void showDatePicker() {
        new DatePickerDialog(
                requireContext(),
                (view, year, month, dayOfMonth) -> {
                    dueDateTime.set(Calendar.YEAR, year);
                    dueDateTime.set(Calendar.MONTH, month);
                    dueDateTime.set(Calendar.DAY_OF_MONTH, dayOfMonth);
                    updateDateTimeFields();
                },
                dueDateTime.get(Calendar.YEAR),
                dueDateTime.get(Calendar.MONTH),
                dueDateTime.get(Calendar.DAY_OF_MONTH)
        ).show();
    }

    private void showTimePicker() {
        new TimePickerDialog(
                requireContext(),
                (view, hourOfDay, minute) -> {
                    dueDateTime.set(Calendar.HOUR_OF_DAY, hourOfDay);
                    dueDateTime.set(Calendar.MINUTE, minute);
                    updateDateTimeFields();
                },
                dueDateTime.get(Calendar.HOUR_OF_DAY),
                dueDateTime.get(Calendar.MINUTE),
                false
        ).show();
    }

    private void updateDateTimeFields() {
        SimpleDateFormat dateFormat = new SimpleDateFormat("EEE, MMM d, yyyy", Locale.getDefault());
        binding.datePicker.setText(dateFormat.format(dueDateTime.getTime()));
        SimpleDateFormat timeFormat = new SimpleDateFormat("HH:mm", Locale.getDefault());
        binding.timePicker.setText(timeFormat.format(dueDateTime.getTime()));
    }

    private void saveTask() {
        String title = binding.titleInput.getText().toString().trim();
        if (title.isEmpty()) {
            Toast.makeText(getContext(), "Title cannot be empty", Toast.LENGTH_SHORT).show();
            return;
        }

        String subject = binding.subjectInput.getText().toString().trim();
        String notes = binding.notesInput.getText().toString().trim();
        int priority = getPriorityFromChipId(binding.priorityGroup.getCheckedChipId());

        if (currentTask == null) {
            // New Task
            TaskEntity newTask = new TaskEntity();
            newTask.title = title;
            newTask.subject = subject;
            newTask.notes = notes;
            newTask.priority = priority;
            newTask.dueAt = dueDateTime.getTimeInMillis();
            newTask.status = "PENDING";
            newTask.completed = false;
            newTask.createdAt = System.currentTimeMillis();
            newTask.updatedAt = System.currentTimeMillis();
            viewModel.insert(newTask);
        } else {
            // Update Task
            currentTask.title = title;
            currentTask.subject = subject;
            currentTask.notes = notes;
            currentTask.priority = priority;
            currentTask.dueAt = dueDateTime.getTimeInMillis();
            currentTask.updatedAt = System.currentTimeMillis();
            viewModel.update(currentTask);
        }
        dismiss();
    }

    private int getPriorityFromChipId(int chipId) {
        if (chipId == binding.chipHigh.getId()) return 2;
        if (chipId == binding.chipMedium.getId()) return 1;
        return 0; // Low
    }

    private int getPriorityChipId(int priority) {
        if (priority == 2) return binding.chipHigh.getId();
        if (priority == 1) return binding.chipMedium.getId();
        return binding.chipLow.getId();
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }
}

package com.example.learnlog.ui.tasks;

enum class TaskFilter {
    ALL,
    DUE,
    COMPLETED
}

package com.example.learnlog.ui.tasks;

enum class TaskSort {
    DUE_DATE,
    PRIORITY,
    TITLE
}

private final TaskRepository taskRepository;
private final UserPreferencesRepository userPreferencesRepository;

private final MutableStateFlow<TaskFilter> _taskFilter;
private final MutableStateFlow<TaskSort> _taskSort;

public final StateFlow<List<TaskEntity>> uiState;

@Inject
public TasksViewModel(TaskRepository taskRepository, UserPreferencesRepository userPreferencesRepository) {
    this.taskRepository = taskRepository;
    this.userPreferencesRepository = userPreferencesRepository;

    _taskFilter = new MutableStateFlow<>(TaskFilter.ALL);
    _taskSort = new MutableStateFlow<>(TaskSort.DUE_DATE);

package com.example.learnlog.ui.tasks;

import androidx.lifecycle.ViewModel;
import androidx.lifecycle.viewModelScope;
import com.example.learnlog.data.TaskEntity;
import com.example.learnlog.data.TaskRepository;
import com.example.learnlog.data.UserPreferencesRepository;
import dagger.hilt.android.lifecycle.HiltViewModel;
import javax.inject.Inject;
import kotlinx.coroutines.flow.Flow;
import kotlinx.coroutines.flow.MutableStateFlow;
import kotlinx.coroutines.flow.StateFlow;
import kotlinx.coroutines.flow.combine;
import kotlinx.coroutines.launch;
import java.util.List;

@HiltViewModel
public class TasksViewModel extends ViewModel {

    private final TaskRepository taskRepository;
    private final UserPreferencesRepository userPreferencesRepository;

    private final MutableStateFlow<TaskFilter> _taskFilter;
    private final MutableStateFlow<TaskSort> _taskSort;

    public final StateFlow<List<TaskEntity>> uiState;

    @Inject
    public TasksViewModel(TaskRepository taskRepository, UserPreferencesRepository userPreferencesRepository) {
        this.taskRepository = taskRepository;
        this.userPreferencesRepository = userPreferencesRepository;

        _taskFilter = new MutableStateFlow<>(TaskFilter.ALL);
        _taskSort = new MutableStateFlow<>(TaskSort.DUE_DATE);

        viewModelScope.launch(() -> {
            userPreferencesRepository.getTaskFilter().collect(filter -> _taskFilter.setValue(filter));
        });

        viewModelScope.launch(() -> {
            userPreferencesRepository.getTaskSort().collect(sort -> _taskSort.setValue(sort));
        });

        uiState = combine(
                taskRepository.getTasks(),
                _taskFilter,
                _taskSort,
                (tasks, filter, sort) -> {
                    return applyFilterAndSort(tasks, filter, sort);
                }
        ).stateIn(viewModelScope, kotlinx.coroutines.flow.SharingStarted.WhileSubscribed(5000), emptyList());
    }

    public void setFilter(TaskFilter filter) {
        viewModelScope.launch(() -> {
            userPreferencesRepository.setTaskFilter(filter);
        });
    }

    public void setSort(TaskSort sort) {
        viewModelScope.launch(() -> {
            userPreferencesRepository.setTaskSort(sort);
        });
    }

    public void toggleComplete(TaskEntity task, boolean completed) {
        viewModelScope.launch(() -> {
            task.completed = completed;
            task.status = completed ? "COMPLETED" : "PENDING";
            task.updatedAt = System.currentTimeMillis();
            taskRepository.update(task);
        });
    }

    public void delete(long id) {
        viewModelScope.launch(() -> {
            taskRepository.delete(id);
        });
    }

    public void insert(TaskEntity task) {
        viewModelScope.launch(() -> {
            taskRepository.insert(task);
        });
    }

    public void update(TaskEntity task) {
        viewModelScope.launch(() -> {
            taskRepository.update(task);
        });
    }

    public void reinsert(TaskEntity task) {
        viewModelScope.launch(() -> {
            taskRepository.insert(task);
        });
    }

    private List<TaskEntity> applyFilterAndSort(List<TaskEntity> tasks, TaskFilter filter, TaskSort sort) {
        // Apply filter
        List<TaskEntity> filteredTasks;
        switch (filter) {
            case DUE:
                filteredTasks = tasks.stream().filter(t -> !t.completed && t.dueAt != null).collect(java.util.stream.Collectors.toList());
                break;
            case COMPLETED:
                filteredTasks = tasks.stream().filter(t -> t.completed).collect(java.util.stream.Collectors.toList());
                break;
            case ALL:
            default:
                filteredTasks = tasks;
                break;
        }

        // Apply sort
        switch (sort) {
            case DUE_DATE:
                return filteredTasks.stream().sorted((t1, t2) -> Long.compare(t1.dueAt != null ? t1.dueAt : Long.MAX_VALUE, t2.dueAt != null ? t2.dueAt : Long.MAX_VALUE)).collect(java.util.stream.Collectors.toList());
            case PRIORITY:
                return filteredTasks.stream().sorted((t1, t2) -> Integer.compare(t2.priority, t1.priority)).collect(java.util.stream.Collectors.toList()); // Desc
            case TITLE:
                return filteredTasks.stream().sorted((t1, t2) -> t1.title.compareToIgnoreCase(t2.title)).collect(java.util.stream.Collectors.toList());
            default:
                return filteredTasks;
        }
    }
}
