package com.example.learnlog
import android.app.Application import androidx.hilt.work.HiltWorkerFactory import androidx.work.Configuration import androidx.work.ExistingPeriodicWorkPolicy import androidx.work.PeriodicWorkRequestBuilder import androidx.work.WorkManager import com.example.learnlog.worker.DailyRollupWorker import com.jakewharton.threetenabp.AndroidThreeTen import dagger.hilt.android.HiltAndroidApp import kotlinx.coroutines.CoroutineScope import kotlinx.coroutines.Dispatchers import kotlinx.coroutines.SupervisorJob import java.util.concurrent.TimeUnit import javax.inject.Inject
@HiltAndroidApp class LearnLogApplication : Application(), Configuration.Provider {
@Inject lateinit var workerFactory: HiltWorkerFactory
private val applicationScope = CoroutineScope(SupervisorJob() + Dispatchers.IO)
override fun onCreate() { super.onCreate() AndroidThreeTen.init(this) scheduleDailyRollupWork() }
override val workManagerConfiguration: Configuration get() = Configuration.Builder() .setWorkerFactory(workerFactory) .build()
private fun scheduleDailyRollupWork() { val rollupWorkRequest = PeriodicWorkRequestBuilder<DailyRollupWorker>( 1, TimeUnit.DAYS ).build()
WorkManager.getInstance(this).enqueueUniquePeriodicWork( "daily_rollup_work", ExistingPeriodicWorkPolicy.KEEP, rollupWorkRequest ) } }

