        try {
            val intent = Intent(Intent.ACTION_SENDTO).apply {
                data = Uri.parse("mailto:${getString(R.string.support_email)}")
                putExtra(Intent.EXTRA_SUBJECT, getString(R.string.email_subject))
                putExtra(Intent.EXTRA_TEXT, getString(R.string.email_body_template, BuildConfig.VERSION_NAME, BuildConfig.VERSION_CODE))
            }
            startActivity(Intent.createChooser(intent, "Send Email"))
        } catch (e: Exception) {
            Snackbar.make(binding.root, "No email app found", Snackbar.LENGTH_SHORT).show()
        }
    }

    private fun rateApp() {
        try {
            val intent = Intent(Intent.ACTION_VIEW, Uri.parse("market://details?id=${requireContext().packageName}"))
            startActivity(intent)
        } catch (e: Exception) {
            // Fallback to web version
            openUrl("https://play.google.com/store/apps/details?id=${requireContext().packageName}")
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
